const App = () => {
  const course = 'Half Stack application development'
  const parts = [
    {
      name: 'Fundamental of React',
      exercises: 10
    },
    {
      name: 'Using props to pass data',
      exercises: 7
    },
    {
      name: 'State of a component',
      exercises: 14
    }
  ];


  return (
    <div>
      <h1><Header course={course}/></h1>
      <Content parts={parts}/>
      <Total parts={parts}/> 
    </div>
  )
}

const Header = (props) => {
  console.log(props)
  return(
    <div>
      <p>
        {props.course}
      </p>
    </div>
  )
}

const Content = (props) => {
  console.log(props)
  console.log(props[0][0].name)
  return(
    <div>
      <Part part={props[0].name} exercise={props[0].exercises} />
      <Part part={props[1].name} exercise={props[1].exercises} />
      <Part part={props[2].name} exercise={props[2].exercises} />
    </div>
  )
}

const Part = (props) => {
  return(
    <div>
      <p>{props.part} {props.exercise}</p>
    </div>
  )
}

const Total = (props) => {
  return(
    <div>
      <p>Number of exercises {props[0].exercises+props[1].exercises+props[2].exercises}</p>
    </div>
  )

}
export default App
